//==========================================================================
//
//	The kerning values (how much space to remove between characters) of the
//	small font.
//
//==========================================================================

const int g_aKerningValues[96] =
{
	5, 4, 4, 0, 2,-1, 1, 6, 4, 4, 2, 0, 4, 4, 4, 4, 2, 2, 2, 2, 2, 2, 2, 2,
	2, 2, 4, 4, 0, 0, 0, 3,-1, 1, 2, 1, 1, 2, 2, 1, 1, 4, 3, 2, 3, 0, 1, 0,
	2, 0, 1, 2, 2, 1, 2, 0, 2, 2, 2, 4, 4, 4, 0, 2,	4, 2, 2, 3, 2, 2, 4, 2,
	2, 6, 5, 3, 6, 0, 2, 2, 2, 2, 4, 3, 4, 2, 2, 0, 2, 2, 3, 3, 4, 3, 0, 0
};


//==========================================================================
//
//	This is the first of 96 display lists for each font character
//
//==========================================================================
unsigned int g_iFontStart=0;


//==========================================================================
//
//	Compile font into display lists
//
//==========================================================================
void FontInit()
{
   GLuint i, j;

   g_iFontStart = glGenLists(96);

   glPixelStorei( GL_UNPACK_ALIGNMENT, 1 );

   for(i=0, j=' '; i<=96; i++, j++)
   {
      glNewList( g_iFontStart + j, GL_COMPILE );

      glBitmap(8, 12, 0.0, 0.0, 8-g_aKerningValues[i], 0.0, g_aFontCharacters[i]);
      glEndList();
   }
}

//==========================================================================
//
//	Print string to window using display lists that call glBitmap()
//
//==========================================================================
void FontDraw(char *txt, int x, int y)
{
	if(g_iFontStart == 0)
	{
		fprintf(stderr,"Error: font has not been initialised\n");
		return;
	}
	glPushAttrib(GL_LIST_BIT);
	glRasterPos2i(x, y);

	while( *txt != '\0' )
	{
		glCallList( (*txt) + g_iFontStart );
		++txt;
	}
	glPopAttrib();
}


//==========================================================================
//
//	Print string to window using display lists that call glBitmap()
//
//==========================================================================
void FontCleanup()
{
	if(g_iFontStart != 0)
		glDeleteLists(g_iFontStart,96);
	g_iFontStart = 0;
}


